class Solution {
    public int numPermsDISequence(String S) {
        int MOD = 1000000007;
        int N = S.length();
        int [] dp = new int [N+1];
        dp[0] =1;
        int sum = 0;
        for(int i =1;i<=N;i++){
            char ch = S.charAt(i-1);
            int [] dp2 = new int[N+1];
            if(ch == 'I'){
                for(int j = 1;j<=i;j++)
                    dp2[j] = (dp2[j-1]+dp[j-1])%MOD;
            }
            else{
                 for(int j = i-1;j>=0;j--)
                    dp2[j] = (dp2[j+1]+dp[j])%MOD;
            }
            dp = dp2;
        }
        int ans = 0;
        for(int num : dp)
            ans = (ans+num)%MOD;
        return ans;
    }
}

