class Solution:
    def numPermsDISequence(self, S: str) -> int:
        M = 10**9 + 7
        C = [1] + [0 for _ in range(len(S))]
        for n, id in enumerate(list(S), start=2):
            if id == 'I':
                C = [sum(C[j - 1] for j in range(1, k)) % M for
                     k in range(1, n + 1)]
            elif id == 'D':
                C = [sum(C[j - 1] for j in range(k, n)) % M for
                     k in range(1, n + 1)]
        return sum(C) % M
