        if not S:
            return 0

        mod = 1000000007
        n = len(S)
        dp = []
        for i in range(n+1):
            dp.append([0] * (n+1))
        if S[0] == 'D':
            dp[1][0] = 1
        elif S[0] == 'I':
            dp[1][1] = 1

        for i in range(1, n+1):
            for j in range(i+1):
                if S[i-1] == 'D':
                    for k in range(j, i):
                        dp[i][j] += dp[i-1][k]
                        dp[i][j] %= mod
                elif S[i-1] == 'I':
                    for k in range(0, j):
                        dp[i][j] += dp[i-1][k]
                        dp[i][j] %= mod

        ret = 0
        for k in range(n+1):
            ret += dp[n][k]
            ret %= mod

        return ret
