class Solution:
    def numPermsDISequence(self, S: str) -> int:
        if not S:
            return 0

        mod = 1000000007
        n = len(S)
        dp = []
        for i in range(n+1):
            dp.append([0] * (n+1))
        dp[0][0] = 1

        for i in range(1, n+1):
            for j in range(i+1):
                if S[i-1] == 'D':
                    if j != 0:
                        dp[i][j] = (dp[i-1][i-1] - dp[i-1][j-1] + dp[i][j-1]) % mod
