const int MOD = 1000000007;

class Solution {
public:
    int numPermsDISequence(string S) {
        /**
            状态定义：   设 f(i,j) 是序列<0..i>以 "第j小元素" 为尾时的有效序列数, 0<=j<=i
            转移方程：   f(i,j) = sum{ f(i-1,k) }, k < j 或 k > j
            边界条件：   f(0,0) = 1
        **/
        int n = S.size();
        vector<int> dp(n+1,0), sum(n+1,0), aux(n+1,0);
        sum[0] = 1;
        for (int i = 1; i <= n; i++) {
            aux[0] = dp[0] = S[i-1]=='I' ? 0 : sum[i-1];
            for (int j = 1; j <= i; j++) {
                dp[j]  = S[i-1]=='I' ? sum[j-1] : (sum[i-1]-sum[j-1]+MOD)%MOD; 
                aux[j] = (aux[j-1] + dp[j]) % MOD;
            }
            swap(aux, sum);
        }
        return sum[n];
    }
};
```

