```C++
// K(s, x) = nums of perms with tail ranking x
// K(s + 'I') = K(s, x) + K(s, x+1) + ... + K(s, s.len)
// K(s + 'D') = K(s, 0) + K(s, 1) + ... + K(s, x - 1)

// G(s, 0) = 0
// G(s, x) = K(s, 0) + K(s, 1) + ... + K(s, x - 1)

// K(s + 'I') = K(s, x) + K(s, x+1) + ... + K(s, s.len) = G(s, s.len + 1) - G(s, x)
// K(s + 'D') = K(s, 0) + K(s, 1) + ... + K(s, x - 1) = G(s, x)

// G(s, x + 1) = G(s, x) + K(s, x)


class Solution {
public:
    int numPermsDISequence(string S) {
        vector<int64_t> G = {0, 1};

        for (int s_len = 0; s_len < S.size(); ++s_len) {
            vector<int64_t> nG = {0};

            for (int x = 0; x <= s_len + 1; ++x) {
                int64_t k = S[s_len] == 'I' ? G[s_len + 1] - G[x] : G[x];
                nG.push_back((nG.back() + k + 1000000007) % 1000000007);
            }

            G.swap(nG);
        }

        return G[S.size() + 1];
    }
};
```

