class Solution {
    public int numPermsDISequence(String S) {
        if (S == null) {
            return 0;
        }
        int mod = (int) 1e9 + 7;
        int n = S.length() + 1;
        int[][] memo = new int[n][n];
        for (int i = 0; i < n; i++) {
            memo[i][0] = 1;
        }
        int acc = 0;
        int up = 0;
        int down = S.length();
        for (int j = 1; j < n; j++) {
            if (S.charAt(j - 1) == 'I') {
                up++;
                for (int i = up; i <= down; i++) {
                    memo[i][j] = (memo[i - 1][j] + memo[i - 1][j - 1]) % mod;
                    acc = memo[i][j];
                }
            } else {
                down--;
                for (int i = down; i >= up; i--) {
                    memo[i][j] = (memo[i + 1][j] + memo[i + 1][j - 1]) % mod;
                    acc = memo[i][j];
                }
            }
        }
        return acc;
    }
}
