class Solution {
public:
    vector<vector<int>> matrixBlockSum(vector<vector<int>>& mat, int K) {
            int n=mat.size(),m=mat[0].size();
        for(int i=0;i<n;i++){
                for(int j=1;j<m;j++){
                        mat[i][j]+=mat[i][j-1];
                }
        }
            for(int i=0;i<m;i++){
                for(int j=1;j<n;j++){
                        mat[j][i]+=mat[j-1][i];
                }
        }
            vector<vector<int> > ans(n,vector<int>(m,0));
            for(int i=0;i<n;i++){
                    for(int j=0;j<m;j++){
                            int x1=max(-1,i-K-1),y1=max(-1,j-K-1),x2=min(n-1,i+K),y2=min(m-1,j+K);
                            if(x1==-1 && y1==-1){
                                    ans[i][j]=mat[x2][y2];
