        mat2 = np.array(mat)
        m,n = len(mat), len(mat[0])

        for i in range(m):
            for j in range(n):
                LCi, UCi = max(0,i-K), min(m,i+K)
                LCj, UCj = max(0,j-K), min(m,j+K)
                mat[i][j] = np.sum(mat2[LCi:UCi+1,LCj:UCj+1])

        return mat
