 class Solution {
 public:
     vector<vector<int>> matrixBlockSum(vector<vector<int>>& mat, int k) {
		 //m and n are the size of the matrix (m * n)
         int m = mat.size(), n = mat[0].size();

		 //see above to know what prefix is
         vector<vector<int>> prefix(m,vector<int> (n));

         for (int i = 0; i < m; i++) {
             int sum = 0;
             for (int j = 0; j < n; j++) {
				//sum is sum of all value of the left of mat[i][j] (include mat[i][j])
                 sum += mat[i][j];
				 //prefix is the sum of all value above and left of prefix[i][j] (include mat([i][j])
                 prefix[i][j] = sum + prefix[max(i - 1,0)][j];
             }
         }

         vector<vector<int>> dp(m,vector<int>(n));
         for (int i = 0; i < m; i++) {
             for (int j = 0; j < n; j++) {
				 //index of the most top value to add
                 int start_i = i - k;
                 //index of the most left value to add
				 int start_j = j - k;

				index of the most bottom value to add
                 int end_i = min(i + k, m - 1);
				 index of the most right value to add 
                 int end_j = min(j + k, n - 1);

                 int extraPart = 0;
				 //if start_i is less than 1 means that there ins't any extra part on the top
                 if (start_i > 0)extraPart += prefix[start_i - 1][end_j];
				 //if start_j is less than 1 means that there isn't any extra part on the left
                 if (start_j > 0)extraPart += prefix[end_i][start_j - 1];

                 //this is the part that extraPart count 2 times (so this part will delete 2 times, instead of 1, so we need to add it back)
                 int PartToAdd = 0;
				 //if start_i or start_j is less than 1 means that there isn't a part that is delete twice
				 //because if start_i is less than 1 means we dind't delete anything of the top, same for start_j (instead of the top, this case the left part)
                 if (start_i > 0 && start_j > 0)PartToAdd += prefix[start_i - 1][start_j - 1];

                 dp[i][j] = prefix[end_i][end_j] - extraPart + PartToAdd;
             }
         }


         return dp;
     }
 };

```

