class Solution:
    def matrixBlockSum(self, mat: List[List[int]], k: int) -> List[List[int]]:
        #calculate the value sum with mat[0][0] as top left corner, and mat[i][j] as the bottom right corner and store in dp matrix
        #res[i][j] is the sum of the square with mat[i-k][j-k] as the top right corner and mat[i+k][j+k] as the bottom right corner
        #square sum of row1co1 as top left and row2col2 as the bottom right = dp[row2 + 1][col2 + 1] - dp[row1][col2 + 1] - dp[row2 + 1][col1] + dp[row1][col1]
        #O(m*n) time and space

        m, n = len(mat), len(mat[0])
        dp = [[0 for _ in range(n + 1)] for _ in range(m + 1)]
        for i in range(m):
            for j in range(n):
