        /*
        1 2 3 
        4 5 6
        7 8 9

        result matrix
        12 21 16
        27 45 33
        24 39 28

        prefix matrix
        1  3  6
        5  12 21
        12 27 45


        */
        int r = mat.size();
        int c = mat[0].size();

        vector<vector<int>> sum = mat, res = mat;

        for (int i = 1; i < c; ++i)
        {
            sum[0][i] += sum[0][i - 1];
        }
        for (int i = 1; i < r; ++i)
        {
            sum[i][0] += sum[i - 1][0];
        }

        for (int i = 1; i < r; ++i)
        {
            for (int j = 1; j < c; ++j)
            {
                sum[i][j] += sum[i - 1][j] + sum[i][j - 1] - sum[i - 1][j - 1];
            }
        }

        for (int i = 0; i < r; ++i)
        {
            for (int j = 0; j < c; ++j)
            {
                int north = i - k <= 0 ? 0 : i - k;
                int south = i + k >= r ? r - 1 : i + k;

                int west = j - k <= 0 ? 0 : j - k;
                int east = j + k >= c ? c - 1 : j + k;

                res[i][j] = sum[south][east];

