class Solution {
    public int[][] matrixBlockSum(int[][] mat, int K) {
        int n = mat.length;
        int m = mat[0].length;
        int[][] temp = new int[n + 1][m + 1];
        for (int i = 0; i < n; i++) {
            temp[i + 1][1] = temp[i][1] + mat[i][0];
        }
        for (int i = 0; i < m; i++) {
            temp[1][i + 1] = temp[1][i] + mat[0][i];
        }
        for (int i = 1; i < n; i++) {
            for (int j = 1; j < m; j++) {
                temp[i + 1][j + 1] = temp[i][j + 1] + temp[i + 1][j] - temp[i][j] + mat[i][j];
            }
        }
        int[][] res = new int[n][m];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                int x1 = i - K >= 0 ? i - K : 0;
                int y1 = j - K >= 0 ? j - K : 0;
                int x2 = i + K + 1<= n ? i + K + 1 : n;
                int y2 = j + K + 1<= m ? j + K + 1 : m;
                res[i][j] = temp[x2][y2] - temp[x2][y1] - temp[x1][y2] + temp[x1][y1];
            }
        }
        return res;
    }
}
