class Solution {
    public int[][] matrixBlockSum(int[][] mat, int K) {
        int m = mat.length;
        int n = mat[0].length;
        int [][]dp = new int[m+1][n+1];
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                dp[i+1][j+1] = mat[i][j] + dp[i][j+1] + dp[i+1][j] - dp[i][j];
            }
        }
        int [][]ans = new int[m][n];
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                int l = Math.max(0, j - K);
                int r = Math.min(n-1, j + K)+1;
                int u = Math.max(0, i - K);
                int d = Math.min(m-1, i + K)+1;
                ans[i][j] = dp[d][r] - dp[d][l] - dp[u][r] + dp[u][l];
            }
        }
        return ans;
    }
}
