class Solution {
public:
    vector<vector<int>> matrixBlockSum(vector<vector<int>>& mat, int k) {
        // prefix sum
        int m= mat.size(), n= mat[0].size(), temp;
        vector<vector<int>> pre(m+1, vector<int> (n+1, 0));

        for(int i=0;i<m;i++)
            for(int j=0;j<n;j++)
                pre[i+1][j+1]= mat[i][j]+pre[i][j+1]+pre[i+1][j]-pre[i][j];


        // creating ans matrix using prefix sum matrix(pre)
        vector<vector<int>> ans(m, vector<int> (n));
        for(int i=0;i<m;i++)
            for(int j=0;j<n;j++){
                int iup= max(0,i-k), idown= min(m-1,i+k), jleft= max(0,j-k), jright= min(n-1, j+k);
                temp= pre[idown+1][jright+1]  -pre[idown+1][jleft]  -pre[iup][jright+1]  +pre[iup][jleft];
                ans[i][j]= temp;
            }

        return ans;
    }
};
