    }

    int sumMat(int k, int[][] mat, int i, int j) {
        int sum = 0;

        for (int a = i - k; a <= i + k; a++)
            if (a >= 0 && mat.length > a)
                for (int b = j - k; b <= j + k; b++)
                    if (b >= 0 && mat[0].length > b)
                        sum+=mat[a][b];

        return sum;
    }
}
