class Solution {
public:
    vector<vector<int>> matrixBlockSum(vector<vector<int>>& mat, int k) {
        int m = mat.size(), n = mat[0].size();
        vector<vector<int>> ans(m, vector<int> (n));
        for(int i = 0; i < m; ++i) {
            for(int j = 1; j < n; ++j) {
                mat[i][j] += mat[i][j - 1];
            }
        }
        for(int j = 0; j < n; ++j) {
            for(int i = 1; i < m; ++i) {
                mat[i][j] += mat[i - 1][j];
            }
        }
        for(int i = 0; i < m; ++i) {
            for(int j = 0; j < n; ++j) {
                int l = j - k, r = j + k, t = i - k, b = i + k;
                int prefixTop = t <= 0 ? 0 : mat[t - 1][min(r, n - 1)];
                int prefixLeft = l <= 0 ? 0 : mat[min(b, m - 1)][l - 1];
                int prefixTopLeft = t > 0 && l > 0 ? mat[t - 1][l - 1] : 0;

                ans[i][j] = mat[min(b, m - 1)][min(r, n - 1)] - prefixTop - prefixLeft + prefixTopLeft; 
            }
        }
        return ans;
    }
};
