        */
        for(int i=0;i<m;i++){
            for(int j=1;j<n;j++){
                mat[i][j] += mat[i][j-1];
            }
        }
        /*
		Prefix sum on rows
        mat[3][3] = {
            {1,3,6},
            {4,9,15},
            {7,15,24}
        };
        */
        for(int i=0;i<n;i++){
            for(int j=1;j<m;j++){
                mat[j][i] += mat[j-1][i];
            }
        }
        /*
		Prefix sum on cols
        mat[3][3] = {
            {1,3,6},
            {5,12,21},
            {12,27,45}
        };
        */
        vector<vector<int>> ans;
        for(int i=0;i<m;i++){
            vector<int> ans_i(n);
            int first_row = i-k,last_row = i+k;
            for(int j=0;j<n;j++){ 
                int first_col = j-k, last_col = j+k;
                last_row = (last_row>m-1)?m-1:last_row;
