class Solution {
public:
    vector<vector<int>> matrixBlockSum(vector<vector<int>>& mat, int k) {
        int n=mat.size();
        int m=mat[0].size();
        vector<vector<int>>dp(n,vector<int>(m,0));
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                dp[i][j]=mat[i][j];
                if(i>0)
                    dp[i][j]+=dp[i-1][j];
                if(j>0)dp[i][j]+=dp[i][j-1];
                if(i>0&&j>0)dp[i][j]-=dp[i-1][j-1];
            }
        }
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                // cout<<dp[i][j]<<" ";
                int lfx=max(0,j-k);
                int rtx=min(m-1,j+k);
                int lfy=max(0,i-k);
