                int rowStart=i-k<0?0:i-k;
                int rowEnd=i+k>=mat.length?mat.length-1:i+k;
                int colStart=j-k<0?0:j-k;
                int colEnd=j+k>=mat[0].length?mat[0].length-1:j+k;
                int sum=0;
                for(int v=rowStart;v<=rowEnd;v++){
                    sum+=mat[v][colEnd];
                    if(colStart!=0) sum-=mat[v][colStart-1];
                }
                ans[i][j]=sum;
            }
        }
        return ans;
    }
