    import sys
    input = sys.stdin.readline

    def matrixBlockSum(self, mat: List[List[int]], k: int) -> List[List[int]]:

        n= len(mat)

        m = len(mat[0])

        pre=[ [ 0 for i in range(m)] for j in range(n) ]


        pre[0][0] = mat[0][0]


        for i in range(1,m):

            pre[0][i] += pre[0][i-1] + mat[0][i]

        for j in range(1,n):

            pre[j][0] += pre[j-1][0] + mat[j][0]



        for i in range(1,n):
            for j in range(1,m):

                pre[i][j] += pre[i-1][j] + pre[i][j-1] - pre[i-1][j-1]+mat[i][j]


        result = [[ 0 for i in range(m)] for j in range(n) ]


        for i in range(n):
