class Solution {
public static int[][] matrixBlockSum(int[][] mat, int k) {
        int r = mat.length;
        int c = mat[0].length;
        int[][] sum = new int[r][c];
        int[][] ans = new int[r][c];
        sum[0][0]=mat[0][0];
        for(int i = 1;i<r;i++) {
            sum[i][0]=mat[i][0]+sum[i-1][0];
        }
        for(int i = 1;i<c;i++) {
            sum[0][i]=mat[0][i]+sum[0][i-1];
        }
        for(int i = 1;i<r;i++) {
            for(int j = 1;j<c;j++) {
               sum[i][j]+=(mat[i][j]+sum[i-1][j]+sum[i][j-1]-sum[i-1][j-1]);
            }   
        }

        int r1,c1,r2,c2;
        // (r1c1) is top left corner and (r2,c2) is bottom-right corner of sub-matrix 

		for(int i = 0;i<r;i++) {
            for(int j = 0;j<c;j++) {
                r1 = Math.max(0,i-k);
                r2 = Math.min(r-1,i+k);
                c1 = Math.max(0,j-k);
                c2 = Math.min(c-1,j+k);
                ans[i][j] = sum[r2][c2];
                if(r1>0 && c1>0) {
                    ans[i][j]-=(sum[r1-1][c2]+sum[r2][c1-1]-sum[r1-1][c1-1]);
                }
                else if(r1>0) {
                    ans[i][j]-=(sum[r1-1][c2]);
                }
                else if(c1>0) {
                    ans[i][j]-=sum[r2][c1-1];
                }
            }
        }
        return ans;
    }	
}
