vector<vector<int>> matrixBlockSum(vector<vector<int>>& m, int K) 
{       
	int N(size(m)), M(size(m[0]));
	for(auto i{0}; i<N; ++i)
		for(auto j{0}; j<M; ++j)
			m[i][j] += (i ? m[i-1][j] : 0) + (j ? m[i][j-1] : 0) - (i and j ? m[i-1][j-1] : 0);

    vector out(N, vector(M, 0));  
	for(auto i{0}; i<N; ++i)
		for(auto j{0}; j<M; ++j)
		{
			auto bi{min(i+K, N-1)}, bj{min(j+K, M-1)}, ui{max(i-K, 0)}, uj{max(j-K, 0)};
			out[i][j] = m[bi][bj] - (ui ? m[ui-1][bj] : 0) - (uj ? m[bi][uj-1] : 0) + (ui and uj ? m[ui-1][uj-1] : 0);
		}

	return out;
}
