class Solution {
public:
    vector<vector<int>> dp;
    vector<vector<int>> matrixBlockSum(vector<vector<int>>& mat, int K) {
        int m = mat.size();
        if(m == 0) return mat;
        int n = mat[0].size();
        if(n == 0) return mat;

        dp = vector<vector<int>> (m+1, vector<int> (n+1, 0));

        for(int i=0;i<m;i++)
        {
            for(int j=0;j<n;j++)
            {
                dp[i+1][j+1] = dp[i][j+1] + dp[i+1][j] - dp[i][j] + mat[i][j];
            }
        }

        for(int i=0;i<m;i++)
        {
            for(int j=0;j<n;j++)
            {
                mat[i][j] = helper(i-K, i+K, j-K, j+K, m, n);
            }
        }
        return mat;
    }
    int helper(int iMin, int iMax, int jMin, int jMax, int m, int n)
    {
        iMin = iMin < 0 ? 0 : iMin;
        iMax = iMax >= m ? m-1 : iMax;
        jMin = jMin < 0 ? 0 : jMin;
        jMax = jMax >= n ? n-1 : jMax;
        return dp[iMax+1][jMax+1] + dp[iMin][jMin] - dp[iMax+1][jMin] - dp[iMin][jMax+1]; 
