class Solution {
public:
    vector<vector<int>> matrixBlockSum(vector<vector<int>>& mat, int k) {
        int n = mat.size(), m = mat[0].size();
        vector<vector<int>> pref_sum(n, vector<int>(m, 0)), res(n, vector<int>(m, 0));
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                pref_sum[i][j] = mat[i][j];
                if (i - 1 >= 0) pref_sum[i][j] += pref_sum[i - 1][j];
                if (j - 1 >= 0) pref_sum[i][j] += pref_sum[i][j - 1];
                if (i - 1 >= 0 && j - 1 >= 0) pref_sum[i][j] -= pref_sum[i - 1][j - 1];
            }
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                int x1 = max(i - k, 0), y1 = max(j - k, 0), x2 = min(i + k, n - 1), y2 = min(j + k, m - 1);
                res[i][j] = pref_sum[x2][y2];
                if (x1 - 1 >= 0) res[i][j] -= pref_sum[x1 - 1][y2];
                if (y1 - 1 >= 0) res[i][j] -= pref_sum[x2][y1 - 1];
                if (x1 - 1 >= 0 && y1 - 1 >= 0) res[i][j] += pref_sum[x1 - 1][y1 - 1];
            }
        }
        return res;
    }
};
