class Solution {
public:
    vector<vector<int>> matrixBlockSum(vector<vector<int>>& arr, int k) {
        int n = arr.size();
        int m = arr[0].size();
        vector<vector<int>> answer(n,vector<int>(m,0));

        //Pre-Computation (PrefixSum-Row)
        for(int i = 0;i<n;++i){
            for(int j = m-1;j>0;--j){
                arr[i][j-1]+=arr[i][j];
            }
        }
        //Pre-Computation (PrefixSum-RowCol)
        for(int i = 0;i<m;++i){
            for(int j = n-1;j>0;--j){
                arr[j-1][i]+=arr[j][i];
            }
        }

        //Answer
        for (int i = 0; i < n; ++i)
    {
        for (int j = 0; j < m; ++j)
        {
            int p = max(0, i - k);
            int q = max(0, j - k);
            answer[i][j] = arr[p][q];
            if (j + k + 1 < m)
            {
                answer[i][j] -= arr[p][j + k + 1];
            }
            if (i + k + 1 < n)
            {
                if (j + k + 1 < m)
                {
                    answer[i][j] -= (arr[i + k + 1][q] - arr[i + k + 1][j + k + 1]);
                }
                else
                {
                    answer[i][j] -= arr[i + k + 1][q];
                }
            }
        }
    }
    return answer;
    }
};
