class Solution {
public:
    vector<vector<int>> matrixBlockSum(vector<vector<int>>& mat, int k) {
        int m = mat.size();
        int n = mat[0].size();

        for(int i=0;i<m;i++)
        {
            for(int j=1;j<n;j++)
            {
                mat[i][j]+=mat[i][j-1];
            }
        }

        for(int i=1;i<m;i++) 
        {
            for(int j=0;j<n;j++) 
            {
                mat[i][j]=mat[i][j]+mat[i-1][j];
            }
        }


        vector<vector<int>>ans(m,vector<int>(n,0));

        for(int i=0;i<m;i++) 
        {
            for(int j=0;j<n;j++) 
            {
                int row_s = i-k;
                int col_s = j-k;
                int row_e = i+k;
                int col_e = j+k;

                if(row_e>=m) row_e=m-1;
                if(col_e>=n) col_e=n-1;

                int tot = mat[row_e][col_e];

