class Solution {
public:
    vector<vector<int>> matrixBlockSum(vector<vector<int>>& mat, int k) {
        int m=mat.size()-1, n=mat[0].size()-1;
        vector<vector<int>> sum_vector(m+1, vector<int>(n+1,0));

        for(int i=0; i<=m; ++i){
            int row = 0;
            for(int j=0; j<=n; ++j){
                row += mat[i][j];
                if(i!=0)    sum_vector[i][j] = sum_vector[i-1][j];
                sum_vector[i][j] += row;
            }
        }

        int rstart, rend, cstart, cend;

        for(int i=0; i<=m; ++i)
            for(int j=0; j<=n; ++j){

                rstart=i-k, rend=i+k, cstart=j-k, cend=j+k;

                if(rstart<0) rstart=0;
                if(rend>m) rend=m;
                if(cstart<0) cstart=0;
                if(cend>n) cend=n;

                if(rstart==0 and cstart==0) mat[i][j] = sum_vector[rend][cend];
                else if(rstart==0) mat[i][j] = sum_vector[rend][cend] - sum_vector[rend][cstart-1];
                else if(cstart==0) mat[i][j] = sum_vector[rend][cend] - sum_vector[rstart-1][cend];
                else mat[i][j] = sum_vector[rend][cend]
                                  - sum_vector[rend][cstart-1] 
                                  - sum_vector[rstart-1][cend]
                                  + sum_vector[rstart-1][cstart-1];
            }

        return mat;
    }
};
