class Solution:
    def matrixBlockSum(self, mat: List[List[int]], k: int) -> List[List[int]]:
        m = len(mat)
        n = len(mat[0])
        s1 = [[0 for j in range(n)] for i in range(m)]
        s2 = [[0 for j in range(n)] for i in range(m)]
        # For each row, do a sliding window summing: add the value that "comes into window"
		# and subtract the value "going out of the window".
        for i in range(m):
            x = sum(mat[i][0:k])
            for j in range(n):
                if j + k < n:
                    x += mat[i][j+k]
                if j - k - 1 >= 0:
                    x -= mat[i][j-k-1]
                s1[i][j] = x
        # Now do the same in columns
        for j in range(n):
            x = sum(s1[z][j] for z in range(min(m, k)))
            for i in range(m):
                if i + k < m:
                    x += s1[i+k][j]
                if i - k - 1 >= 0:
                    x -= s1[i-k-1][j]
                s2[i][j] = x
        return s2
