class Solution {
    public int[][] matrixBlockSum(int[][] prefix, int k) {
        int m = prefix.length;
        int n = prefix[0].length;
        for (int i = 1; i < n;i++) prefix[0][i] += prefix[0][i-1];
        for (int i = 1; i < m; i++){
            prefix[i][0] += prefix[i-1][0];
            for (int j = 1; j < n;j++) prefix[i][j] += prefix[i-1][j] + prefix[i][j -1] - prefix[i-1][j-1];
        }int[][] res = new int[m][n];

        for (int i = 0; i < m; i++){
            for (int j = 0; j < n; j++){
                int ar = Math.min(i + k, m - 1);
                int ac = Math.min(j + k, n - 1);
                int ir = i - k - 1;
                int ic = j - k - 1;
                int m1 = ir >= 0? prefix[ir][ac] : 0;
                int m2 = ic >= 0? prefix[ar][ic] : 0;
                int a1 = ir >= 0 && ic >= 0? prefix[ir][ic] : 0;
                res[i][j] += prefix[ar][ac] - m1 - m2 + a1;        
            }
        }return res;
    }
}
