class Solution {
public:
    vector<vector<int>> matrixBlockSum(vector<vector<int>>& mat, int k) {
        int m = mat.size(), n = mat[0].size();
        vector<vector<int>> res(m, vector<int>(n)), dp(m, vector<int>(n));
        dp = mat;
        for (int i = 0; i < m; ++i) { //build dp for row
            for (int j = 1; j < n; ++j) {
                dp[i][j] += dp[i][j-1];
            }
        }
        for (int j = 0; j < n; ++j) {
            for (int i = 1; i < m; ++i) { //build dp for column
                dp[i][j] += dp[i-1][j];
            }
        }
        for (int i = 0; i < m; ++i) { //need to check if left top index is zero or not
            int i_min = i - k < 0 ? 0 : i - k;
            int i_max = i + k >= m ? m - 1 : i + k;
            for (int j = 0; j < n; ++j) {
                int j_min = j - k < 0 ? 0 : j - k;
                int j_max = j + k >= n ? n - 1 : j + k;
                if (!i_min && !j_min) res[i][j] = dp[i_max][j_max];
                else if (!i_min) res[i][j] = dp[i_max][j_max] - dp[i_max][j_min-1];
                else if (!j_min) res[i][j] = dp[i_max][j_max] - dp[i_min-1][j_max];
                else res[i][j] = dp[i_max][j_max] - dp[i_max][j_min-1] - dp[i_min-1][j_max] + dp[i_min-1][j_min-1];
            }
        }
        return res;
    }
};
