class Solution {
	public int[][] matrixBlockSum(int[][] mat, int k) {
		int m = mat.length;
		int n = mat[0].length;
		int[][] result = new int[m][n];

		// the horizontal presum matrix where preSum[i][j] holds 
		// the sum of mat[i][0] + ... + mat[i][j-1]
		// note that preSum[i][0] is 0 so that preSum[i][j2+1] - preSum[i][j1]
		// would give us the sum of mat[i][j1] + ... + mat[i][j2]
		int[][] preSumH = new int[m][n+1];

		for(int i = 0; i < m; i++) {
			for(int j = 1; j <= n; j++) {
				preSumH[i][j] = preSumH[i][j-1] + mat[i][j-1];
			}
		}

		// populate the first row of the result
		for(int j = 0; j < n; j++) {
			for(int x = 0; x <= k; x++) {
				result[0][j] += preSumH[x][Math.min(n, j+k+1)] - preSumH[x][Math.max(0, j-k)];
			}
		}

		// finally the recurrence relationship is that result[i][j] is simply result[i-1][j]
		// removing the sum of top row of result[i-1][j] block
		// and adding the sum of bottom row of result[i][j] block
		for(int i = 1; i < m; i++) {
			for(int j = 0; j < n; j++) {
				result[i][j] = result[i-1][j];
				if(i - 1 - k >= 0) {
					result[i][j] -= preSumH[i - 1 - k][Math.min(n, j+k+1)] - preSumH[i - 1 - k][Math.max(0, j-k)];
				}
				if(i + k < m) {
					result[i][j] += preSumH[i+k][Math.min(n, j+k+1)] - preSumH[i+k][Math.max(0, j-k)];
				}
			}
		}
		return result;
	}
}

