class Solution {
    public int[][] matrixBlockSum(int[][] mat, int k) {
        int[][] matSum = new int[mat.length + 1][mat[0].length + 1];
        int[][] retMat = new int[mat.length][mat[0].length];

        for (int i = 0; i < mat.length; i++) {
            int sum = 0;
            for (int j = 0; j < mat[0].length; j++) {
                sum += mat[i][j];
                matSum[i + 1][j + 1] = sum + matSum[i][j + 1]; 
            }
        }

        for (int i = 0; i < mat.length; i++) {
            for (int j = 0; j < mat[0].length; j++) {
                int rb = Math.min(j + k, mat[0].length - 1);
                int lb = Math.max(j - k, 0);
                int tb = Math.max(i - k, 0);
                int bb = Math.min(i + k, mat.length - 1);

                retMat[i][j] = matSum[bb + 1][rb + 1] - matSum[tb][rb + 1] - matSum[bb + 1][lb] + matSum[tb][lb];
            }
        }

        return retMat;
    }
}
