        }
        for(int i = 1; i < m; ++i) {
            for(int j = 0; j < n; ++j) 
                sum[i][j] += sum[i - 1][j];
        }
        for(int i = 0; i < m; ++i) {
            for(int j = 0; j < n; ++j) {
                int max_r = i + k >= m ? m - 1 : i + k;
                int max_c = j + k >= n ? n - 1 : j + k;
                res[i][j] = sum[max_r][max_c];
                if(j - k > 0) res[i][j] -= sum[max_r][j - k - 1];
                if(i - k > 0) res[i][j] -= sum[i - k - 1][max_c];
                if((i - k > 0) && (j - k > 0)) res[i][j] += sum[i - k - 1][j - k - 1];
            }
        }
        return res;
