class Solution {
public:
    vector<vector<int>> matrixBlockSum(vector<vector<int>>& matrix, int k) {

        int n=matrix.size();
        int m=matrix[0].size();
        vector<vector<int>>ans(n,vector<int>(m,0));

        vector<vector<int>>row,col;


        //prefix sum row-wise
        for(int i=0;i<n;i++)
        {
            int sum=0;
            vector<int>v;
            for(int j=0;j<m;j++)
            {
               sum+=matrix[i][j];
               v.push_back(sum);
            }
            row.push_back(v);
        }


        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                 int start=max(i-k,0);//row start pos
                 int end=min(i+k+1,n);//row end pos

                 int start1=max(j-k,0);//col start pos
                 int end1=min(j+k,m-1);//col end pos



                      int t=0;
                for(int f=start;f<end;f++)
                {

                    if(start1-1>=0)
                    {
                        t-=row[f][start1-1];
                    }

                    t+=row[f][end1];
                }
                ans[i][j]=t;
            }
        }

        return ans;
    }
};
