            let sum = 0;
            for (let j = 0; j < M[i].length; j++) {
                sum += M[i][j];
                memo[i][j] = sum;
            }
        }

        // rectangular sums
        for (let i = 1; i < M.length; i++) {
            for (let j = 0; j < M[i].length; j++) {
                memo[i][j] += memo[i - 1][j];
            }
        }

        for (let i = 0; i < m; i++) {
            for (let j = 0; j < n; j++) {
                let iminusK = i - K - 1; // this is only for subtracting top/left/top-left rectangle
                let jminusK = j - K - 1; // this is only for subtracting top/left/top-left rectangle
                let iplusK = Math.min(i + K, m - 1); // take as much as possible on down
                let jplusK = Math.min(j + K, n - 1); // take as much as possible on right

                // prettier-ignore
                {
                    let SE =                                memo[iplusK] [jplusK]     ;
                    let NE = iminusK >= 0                 ? memo[iminusK][jplusK]  : 0;
                    let SW =                 jminusK >= 0 ? memo[iplusK][jminusK]  : 0;
                    let NW = iminusK >= 0 && jminusK >= 0 ? memo[iminusK][jminusK] : 0;
                    M[i][j] = SE - (NE + SW - NW);
                }
            }
        }

        return M;
    }
}

var matrixBlockSum = function (M, K) {
    let dp = new DP();
    return dp.matrixBlockSum(M, K);
};
