class DP {
    matrixBlockSum(M, K) {
        let m = M.length;
        let n = M[0].length;

        // let to right sums  (prefix sums)
        let memo = Array.from({ length: m + 1 }, () => new Array(n + 1).fill(0));
        for (let i = 0; i < M.length; i++) {
            let sum = 0;
            for (let j = 0; j < M[i].length; j++) {
                sum += M[i][j];
                memo[i + 1][j + 1] = sum;
            }
        }

        // rectangular sums
        for (let i = 1; i < M.length; i++) {
            for (let j = 0; j < M[i].length; j++) {
                memo[i + 1][j + 1] += memo[i][j + 1];
            }
        }

        for (let i = 1; i <= m; i++) {
            for (let j = 1; j <= n; j++) {
                let iminusK = Math.max(i - K - 1, 0); // this is only for subtracting top/left/top-left rectangle
                let jminusK = Math.max(j - K - 1, 0); // this is only for subtracting top/left/top-left rectangle
                let iplusK = Math.min(i + K, m); // take as much as possible on down
                let jplusK = Math.min(j + K, n); // take as much as possible on right

                {
                    let SE = memo[iplusK][jplusK];
                    let NE = memo[iminusK][jplusK];
                    let SW = memo[iplusK][jminusK];
                    let NW = memo[iminusK][jminusK];

                    M[i - 1][j - 1] = SE - (NE + SW - NW);
                }
            }
        }

        return M;
    }
}

var matrixBlockSum = function (M, K) {
    let dp = new DP();
    return dp.matrixBlockSum(M, K);
};
