class Solution:
    def matrixBlockSum(self, mat: List[List[int]], k: int) -> List[List[int]]:
        m, n = len(mat), len(mat[0])


        for row in range(m):
            for col in range(n):
                if row == 0:
                    continue
                else:
                    mat[row][col] = mat[row-1][col] + mat[row][col]

        for row in range(m):
            for col in range(n):
                if col == 0:
                    continue
                else:
                    mat[row][col] = mat[row][col-1] + mat[row][col]

        ans = [[0] * n for _ in range(m)]

        for row in range(m):
            for col in range(n):
                n_row = min(row + k, m - 1)
                n_col = min(col + k, n - 1)
                right_top_row = row - k - 1
                right_top_col = n_col

                left_bot_row = n_row
                left_bot_col = col - k - 1

                left_top_row = row - k - 1
                left_top_col = col - k - 1

                target_val = mat[n_row][n_col]
                right_top = 0
                left_bot = 0
                left_top = 0

                if right_top_row >= 0:
                    right_top = mat[right_top_row][right_top_col]

                if left_bot_col >= 0:
                    left_bot = mat[left_bot_row][left_bot_col]

                if left_top_row >= 0 and left_top_col >= 0:
                    left_top = mat[left_top_row][left_top_col]

                ans[row][col] = target_val - right_top - left_bot + left_top
        return ans
