class Solution {
public:
    vector<vector<int>> matrixBlockSum(vector<vector<int>>& mat, int k) {
        int n = mat.size(), m = mat[0].size();
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                int left = (j==0) ? 0 : mat[i][j-1]; //top rectangle
                int top = (i==0) ? 0 : mat[i-1][j]; //left rectangle
                int diag = (i==0 || j==0) ? 0 : mat[i-1][j-1]; // top-left square
                mat[i][j] += left + top - diag;
            }
        }
        vector<vector<int>>ans(n, vector<int>(m,0));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                int i1 = max(i-k,0) , j1 = max(j-k,0);    // top indices
                int i2 = min(i+k,n-1) , j2 = min(j+k,m-1);  // bottom indices

                int left = (j1==0) ? 0 : mat[i2][j1-1];
                int top = (i1==0) ? 0 : mat[i1-1][j2];
                int diag = (i1==0 || j1==0) ? 0 : mat[i1-1][j1-1];
                ans[i][j] = mat[i2][j2] - top - left + diag;    
            }
        }
        return ans;
    }
};
