        def find_sum(x2,y2,x1,y1,mat):

            x2,y2 = min(len(mat)-1,x2),min(len(mat[0])-1,y2)
            x1,y1 = max(x1,0),max(y1,0)

            sm = mat[x2][y2]

            if x1-1 >= 0:
                sm -= mat[x1-1][y2]

            if y1-1 >= 0:
                sm -= mat[x2][y1-1]

            if x1-1 >=0 and y1-1 >=0:
                sm += mat[x1-1][y1-1]

            return sm


        def block_sum(mat,k):

            ans = []

            for i in range(len(mat)):

                temp = []

                for j in range(len(mat[0])):

                    temp.append(find_sum(i+k,j+k,i-k,j-k,mat))

                ans.append(temp)

            return ans


        ult_mat = prefix_col(prefix_sum(mat))
        return block_sum(ult_mat,K)

