class Solution {
public:
    vector<vector<int>> matrixBlockSum(vector<vector<int>>& mat, int k) {

        if(mat.empty())
            return {};

        int n = mat.size();
        int m = mat[0].size();

        for(int i=1;i<n;i++)
            mat[i][0] += mat[i-1][0];

        for(int j=1;j<m;j++)
            mat[0][j] += mat[0][j-1];

        for(int i=1;i<n;i++) {
            for(int j=1;j<m;j++)
                mat[i][j] += mat[i-1][j] + mat[i][j-1] - mat[i-1][j-1];
        }

        vector<vector<int> >res(n,vector<int>(m));

        for(int i=0;i<n;i++) {
            for(int j=0;j<m;j++) {

                int upI = min(n-1,i+k);
                int upJ = min(m-1,j+k);

                res[i][j] = mat[upI][upJ];

                if(j-k-1 >= 0)
                    res[i][j] -= mat[upI][j-k-1];

                if(i-k-1 >= 0)
                    res[i][j] -= mat[i-k-1][upJ];

                if(j-k-1 >= 0 && i-k-1 >= 0)
                    res[i][j] += mat[i-k-1][j-k-1];
            }
        }
        return res;
    }
};
```

