class Solution {
public:
    int findLen(ListNode *head){
        int len=0;
        while(head){
            len++;
            head=head->next;
        }
        return len;
    }
    ListNode* swapNodes(ListNode* head, int k) {
        int n=findLen(head);  // first calculate lenght of Linked_List

        // if the kth node is middle element then we dont need to swap it
		if((2*k-1)==n)return head;


        // Initialise two pointer Node which will point to the kth Node and (k-1)th node from start as we require x_prev to update (k-1)th next to correct Node
        ListNode *x=head;
        ListNode *x_prev=NULL;  
        int cnt=k-1; // here we initialise cnt with k-1 to get kth node as we take 0 index LL
        while(cnt--){
            x_prev=x;
            x=x->next;
        }

//         similarly initialise two pointer node which will point to kth ndie and (k-1)th node from end
        ListNode *y=head;
        ListNode *y_prev=NULL;
        cnt=n-k;
        while(cnt--){
            y_prev=y;
            y=y->next;
        }

//         now x node point to kth node and x_prev point to (k-1)th node form start and y and y_prev from end

//         now just swap the nodes
        if(x_prev){
            x_prev->next=y;
        }
        if(y_prev){
            y_prev->next=x;
        }
        ListNode *t=x->next;
        x->next=y->next;
        y->next=t;


//         here if k==1 then y node point to head
        if(k==1)return y;
//         if k==n then x node point to head

//         otherwise return head
        if(k==n)return x;
        return head;


    }
};
```

