class Solution {
public:
    ListNode* swapNodes(ListNode* head, int k) 
    {
        // initializing the left and right nodes that we will be going to swap
        ListNode* left = head;
        ListNode* right = head;

        ListNode* curr = head;          // taking the head of LL in a temp node for iterating

        // these are the counters that will count the value from beggining and ending
        int beg_cnt = 1;
        int end_cnt = 1;

        // deriving the left kth value from beggining
        while(curr)
        {
            // while does not reach to the kth value
            if(beg_cnt < k)
            {
                left = left->next;
            }

            beg_cnt++;
            end_cnt++;
            curr = curr->next;
        }

        curr = head;
        beg_cnt = 1;

        // deriving the right kth value from the end
        while(curr)
        {
            // while does not reach to the ((total-k)==Kth value from ending)
            if(beg_cnt < (end_cnt-k))
            {
                right = right->next;
            }
            beg_cnt++;
            curr = curr->next;
        }

        // swapping both left and right
        int temp = left->val;
        left->val = right->val;
        right->val = temp;

        return head;
    }
};
