class Solution {
public:
    ListNode* swapNodes(ListNode* head, int k) {
        vector<int> v;   //vector v to store all values of linked list
        ListNode *cur=head;
        while(cur!=NULL){
            v.push_back(cur->val);
            cur=cur->next;
        }
        swap(v[k-1],v[v.size()-k]);   //now swap kth value of vector
        ListNode *ans=head;      
        head=ans;                       
        int i=0;
        while(ans!=NULL){        //changing list values to updated swaped values 
            ans->val=v[i];
            i++;
            ans=ans->next;
        }
        return head;              //finally returning head 
    }
};
