impl Solution {
    pub fn swap_nodes(head: Option<Box<ListNode>>, k: i32) -> Option<Box<ListNode>> {
        let mut v = Vec::new();
        {
            let mut node = &head;
            while let Some(n) = node {
                v.push(n.val);
                node = &n.next;
            }
        }
        let len = v.len();
        v.swap(k as usize - 1, len - k as usize);
        let mut answer = None;
        for &n in v.iter().rev() {
            answer = Some(Box::new(ListNode {
                val: n,
                next: answer,
            }));
        }
        answer
    }
}
