    public ListNode swapNodes(ListNode head, int k) {
        ListNode first = null;
        ListNode last = null;
        int i = 1;
        ListNode node = head;
        while (node != null){
            if (i == k){
                first = node;
                last = head;
            }
            else if (i > k){
                last = last.next;
            }
            node = node.next;
            i++;
        }

        int temp = first.val;
        first.val = last.val;
        last.val = temp;

        return head;
    }
}
