class Solution {
    static ListNode NthFromEnd(ListNode head, int n) {
        if(head==null || head.next==null) return null;    
        int c=0;                                          
        ListNode temp=head;
        while(temp!=null){
            temp=temp.next;
            c++;
        }
        if(n==c) return head.next;
        for(int i=0; i<c-n-1; i++) head=head.next;
        return head;

    }

    public ListNode swapNodes(ListNode head, int k) {
        if(head==null || head.next==null) return head;
        ListNode ptr=head;
        for(int i=0; i<k-1; i++) ptr=ptr.next;
        ListNode last=NthFromEnd(head, k-1);
        int temp=ptr.val;
        ptr.val=last.val;
        last.val= temp;
        return head;

    }
}

