        ListNode tmpPtr=ptr1;
        ListNode ptr2=head;

        while(tmpPtr.next != null){
            tmpPtr=tmpPtr.next;
            ptr2=ptr2.next;
        }
        int temp=ptr1.val;
        ptr1.val=ptr2.val;
        ptr2.val= temp;
        return head;
    }
}
