    vector<int> sortArrayByParityII(vector<int>& nums) {
        vector<int> ans;
        int k=1, m=0;
        for(int i=0; i < nums.size(); i++)
        {
            if(nums[i] & 1) //odd
            {
                ans[k]= nums[i];
                k=k+2;
            }
            else//even
            {
                ans[m]=nums[i];
                m=m+2;
            }
        }
        return ans;
    }
};
