class Solution {
    public int[] sortArrayByParityII(int[] nums) {
        Stack<Integer> evens = new Stack<>();
        Stack<Integer> odds = new Stack<>();

        for (int n : nums) {
            if (n % 2 == 0) evens.add(n);
            else odds.add(n);
        }

        for (int i = 0; i < nums.length; i++) {
            if (i % 2 == 0) nums[i] = evens.pop();
            else nums[i] = odds.pop();
        }

        return nums;
    }
}

// TC: O(n), SC: O(n)
